import React, { useState } from "react";

function CreateSubjectForm() {

    const [subjectInfo, setSubjectInfo] = useState({subjectid: 1, subject: "", categorie: "", vote1: "", vote2: "", vote3: ""});
    let subject;
    let categorie;
    let vote1;
    let vote2;
    let vote3;

    function handleChange(event){
        const target = event.target;
        const value = target.value;
        const name = target.name;
        setSubjectInfo({[name]: value});
    }

    function handleSubmit(e) {
        const nextSubject = {}
        setSubjectInfo({subjectid: subjectInfo.subjectid + 1});
        console.log(subjectInfo)
        localStorage.setItem(subjectInfo.subjectid, subjectInfo);
        e.preventDefault();
    }

    return (
        <div id="container">
            <div id="content">
                <form onSubmit={handleSubmit}>
                    <input
                        value={subject}
                        onChange={handleChange}
                        placeholder="Onderwerp"
                        type="text"
                        name="subject"
                        required
                    />
                    <input
                        value={categorie}
                        onChange={handleChange}
                        placeholder="Categorie"
                        type="text"
                        name="categorie"
                        required
                    />
                    <input
                        value={vote1}
                        onChange={handleChange}
                        placeholder="Stem 1"
                        type="text"
                        name="vote1"
                        required
                    />
                    <input
                        value={vote2}
                        onChange={handleChange}
                        placeholder="Stem 2"
                        type="text"
                        name="vote2"
                        required
                    />
                    <input
                        value={vote3}
                        onChange={handleChange}
                        placeholder="Stem 3"
                        type="text"
                        name="vote3"
                        required
                    />
                    <button type="submit">CreeÃ«r Stemming!</button>
                </form>
            </div>
        </div>
    );

}

export default CreateSubjectForm;
