import React, { useState } from "react";


function Cola(props){
    const [isShown, setIsShown] = useState(false);

    const handleClick = event => {

        setIsShown(true);
    };

    return (
        <div>
            {!isShown && (
                <div>
                    <label>
                        <input type="radio" name={props.colaBrands[0]} value={props.voteCount.count + 1} checked={props.selectedOption === "Coca"} onChange={props.handleEvent}/>
                        Coca cola
                    </label>
                    <label>
                        <input type="radio" name={props.colaBrands[1]} value={props.voteCount.count + 1} checked={props.selectedOption === "Pepsi"} onChange={props.handleEvent}/>
                        Pepsi cola
                    </label>
                    <label>
                        <input type="radio" name={props.colaBrands[2]} value={props.voteCount.count + 1} checked={props.selectedOption === "drpepper"} onChange={props.handleEvent}/>
                        Dr. Pepper
                    </label>
                    <br></br>
                    <button onClick={handleClick}>Stem!</button>
                </div>
            )}
            {isShown && 
                (<p>
                    Resultaten:
                    <br></br>
                    {props.colaBrands[0]} cola: {props.voteCount}<br></br>
                    {props.colaBrands[1]} cola: {props.voteCount}<br></br>
                    {props.colaBrands[2]}: {props.voteCount}
                </p>)}
        </div>
    );
}

export default Cola;
