import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Cola from '../../components/Cola';

function Subject() {

    const [isShown, setIsShown] = useState(false);
    const [selectedOption, setSelectedOption] = useState('');
    const colaCountValues = {coca: 3, pepsi: 0, drPepper: 0};
    const [colaCount, setColaCount] = useState(colaCountValues);
    /*
    const [cocaCount, setCocaCount] = useState(0);
    const [pepsiCount, setPepsiCount] = useState(0);
    const [drPepperCount, setDrPepperCount] = useState(0);
    */

    function countUpCola(event){
        if (selectedOption === "coca"){
            setColaCount(colaCount.coca + 1);
        }
        else if (selectedOption === "pepsi"){
            setColaCount(colaCount.pepsi + 1);
        }
        else if (selectedOption === "drpepper"){
            setColaCount(colaCount.drPepper + 1);
        }
    }


    function handleClick(){
        setIsShown(current => !current);
    }

    function handleEvent(event){
        setSelectedOption(event.target.value);
    }


    return (
        <div id="subjectitems">
        <h3>Drinken</h3>
        <p>Iedereen heeft altijd een mening over welke drankjes het lekkerste is. Dit zal eens en voor al verleden tijd zijn!</p>
        <br></br>
        <ul>
            <li>
                <button onClick={handleClick}>Cola</button>
                {isShown && (<Cola selectedOption={selectedOption} handleEvent={handleEvent} colaCount={colaCount} countUpCola={countUpCola}/>)}
            </li>
            <li>
                <Link>Water</Link>
            </li>
            <li>
                <Link>Bier</Link>
            </li>
        </ul>
    </div>
    );
}

// Dit bevat een lijst met webpagina's welke leiden tot een stem pagina van het gekozen onderwerp.
// Onderwerpen: Eten, Drinken, Wonen, Vrije tijd en Films

export default Subject;
