function CreateSubjectForm() {

    const [subjectInfo, setSubjectInfo] = useState({subject: "", categorie: "", vote1: "", vote2: "", vote2: ""})
    let firstName
    let lastName
    let email
    let password

    function handleChange(event){
        const target = event.target;
        const value = target.value;
        const name = target.name;
        setUserInfo({[name]: value})
    }

    return (
        <div id="container">
            <div id="content">
                <form>
                    <input
                        value={firstName}
                        onChange={handleChange}
                        placeholder="Voornaam"
                        type="text"
                        name="firstname"
                        required
                    />
                    <input
                        value={lastName}
                        onChange={handleChange}
                        placeholder="Achternaam"
                        type="text"
                        name="lastname"
                        required
                    />
                    <input
                        value={email}
                        onChange={handleChange}
                        placeholder="E-mailadres"
                        type="email"
                        name="email"
                        required
                    />
                    <input
                        value={password}
                        onChange={handleChange}
                        placeholder="Wachtwoord"
                        type="password"
                        name="password"
                        required
                    />
                    <button type="submit">Registreer</button>
                </form>
            </div>
        </div>
    );

}

export default CreateSubjectForm;
