    const [selectedOption, setSelectedOption] = useState('');
    const colaBrands = [{name: "Coca", count: 0}, {name: "Pepsi", count: 0}, {name: "Dr Pepper", count: 0},];
    const [voteCountCola, setVoteCount] = useState(colaBrands);

