            <li>
                <button onClick={handleClick}>Cola</button>
                {isShown && (<Votings selectedOption={selectedOption} handleEvent={handleEvent} voteCount={voteCount} setVoteCount={setVoteCount}/>)}
            </li>
            <li>
                <Link>Water</Link>
            </li>
