import React, { useState } from "react";

function LoginForm(){
    const [userInfo, setUserInfo] = useState({firstname: "", lastname: "", email: "", password: ""});
    let email;
    let password;

    function handleSubmit(){
        const userLogin = localStorage.getItem(email);
        if (userLogin.password === password){
            userLogin.isloggedin = true;
            localStorage.setItem(userLogin.email, userLogin);
        }
    }

    return (
        <div id="container">
            <div id="content">
                <form onSubmit={handleSubmit}>
                    <input
                        value={email}
                        placeholder="E-mailadres"
                        type="email"
                        name="email"
                        required
                    />
                    <input
                        value={password}
                        placeholder="Wachtwoord"
                        type="password"
                        name="password"
                        required
                    />
                    <button type="submit">Registreer</button>
                </form>
            </div>
        </div>
    );

}

export default LoginForm;
