import React, { useState } from "react";


function Cola(props){

    const [isShown, setIsShown] = useState(false);
    const [cocaCount, setCocaCount, pepsiCount, setPepsiCount, drPepperCount, setDrPepperCount] = useState(0, 0, 0);

    function countUpCola(props){
        if (props.value === "coca"){
            setCocaCount(cocaCount+1);
        }
        else if (props.value === "pepsi"){
            setCocaCount(pepsiCount+1);
        }
        else if (props.value === "drpepper"){
            setCocaCount(drPepperCount+1);
        }
    }

    const handleClick = event => {
        setIsShown(current => !current);
    };

    return (
        <div>
            <label>Cola</label>
            <label>
                <input type="radio" name="Cola" value="coca" checked={props.selectedOption === "coca"} onChange={props.handleEvent}/>
                Coca cola
            </label>
            <label>
                <input type="radio" name="Cola" value="pepsi" checked={props.selectedOption === "pepsi"} onChange={props.handleEvent}/>
                Pepsi cola
            </label>
            <label>
                <input type="radio" name="Cola" value="drpepper" checked={props.selectedOption === "drpepper"} onChange={props.handleEvent}/>
                Dr. Pepper
            </label>
            <br></br>
            <button onClick={function(event){countUpCola(); handleClick();}}>Stem!</button>
            {isShown && 
                (<p>
                    Resultaten:
                    <br></br>
                    Coca cola: {this.state.cocaCount};<br></br>
                    Pepsi cola: {this.state.pepsiCount};<br></br>
                    Dr. Pepper: {this.state.drPepperCount};
                </p>)}
        </div>
    );
}

// 

export default Cola;
