import React, { useState } from "react";


function Cola(selectedOption, handleEvent, voteCount, setVoteCount){
    const [isShown, setIsShown] = useState(false);

    const handleClick = event => {
        setIsShown(true);
    };

    return (
        <div>
            {!isShown && (
                <div>
                    {voteCount.map((voteCount, index) => (
                    <label>
                    <input type="radio" name={voteCount.name[0]} value={voteCount.name[0]} checked={selectedOption === "Coca"} onChange={handleEvent}/>
                    {voteCount.name[0]}
                </label>
                    ))}

                    <label>
                        <input type="radio" name={voteCount.name[1]} value={voteCount.name[1]} checked={selectedOption === "Pepsi"} onChange={handleEvent}/>
                        {voteCount.name[1]}
                    </label>
                    <label>
                        <input type="radio" name={voteCount.name[2]} value={voteCount.name[2]} checked={selectedOption === "Dr Pepper"} onChange={handleEvent}/>
                        {voteCount.name[2]}
                    </label>
                    <br></br>
                    <button onClick={handleClick}>Stem!</button>
                </div>
            )}
            {isShown && 
                (<p>
                    Resultaten:
                    <br></br>
                    {voteCount.name[0]} cola: {voteCount.coca}<br></br>
                    {voteCount.name[1]} cola: {voteCount.pepsi}<br></br>
                    {voteCount.name[2]}: {voteCount.drpepper}
                </p>)}
        </div>
    );
}

export default Cola;
