import React, { useState } from 'react';
import Votings from '../../components/Votings';
import VoteData from '../../data/VoteData';

function Subject(subject) {
    const shownSubjects = [{name: "", shown: false}];
    categorieData.map((items) => 
        shownSubjects.append({name: items.name, shown: false})
    );
    const [categorieData, setCategorieData] = useState([]);
    const [voteCount, setVoteCount] = useState(categorieData.data);
    const [isShown, setIsShown] = useState(shownSubjects);
    const [selectedOption, setSelectedOption] = useState('');

    function handleClick(event){
        setIsShown(
            isShown.map((show) =>
                event.target.name === show.name
                    ? { ...show, shown: true}
                    : { ...show, shown: false}
            )
        );
    }

    function handleEvent(event){
        setSelectedOption(event.target.name);
    }

    return (
        <div id="subjectitems">
            <h3>{subject.subjectname}</h3>
            <p>{subject.subjectdescription}</p>
            <VoteData categorieData={categorieData} setCategorieData={setCategorieData} subjectdata={subject}/>
            <ul>
            {isShown.map((show, index) => (
                <li key={index}>
                    <button onClick={handleClick} name={show.name}>{show.name}</button>
                    {show.shown && (<Votings 
                                    selectedOption={selectedOption}
                                    handleEvent={handleEvent}
                                    voteCount={show.name === shown ? voteCountCola : show.name === "Water" ? voteCountWater : voteCountBeer}
                                    setVoteCount={show.name === "Cola" ? setVoteCountCola : show.name === "Water" ? setVoteCountWater : setVoteCountBeer}/>)}
                </li>
            ))}
        </ul>
        </div>
    );
}

// Onderwerpen: Eten, Drinken, Wonen, Vrije tijd en Films

export default Subject;
