import React, { useState } from 'react';
import Votings from '../../components/Votings';
import VoteData from '../../data/VoteData';

function Subject(subject) {
    <VoteData setCategorieData={setCategorieData} subjectdata={subject}/>

    const [categorieData, setCategorieData] = useState([]);
    const [shownSubjects, setShownSubjects] = useState([{name: "", shown: false}]);
    categorieData.map((items) => 
        setShownSubjects({name: items.name, shown: false})
    );
    const [voteCount, setVoteCount] = useState(categorieData);
    const [isShown, setIsShown] = useState(shownSubjects);
    const [selectedOption, setSelectedOption] = useState('');

    function handleClick(event){
        setIsShown(
            isShown.map((show) =>
                event.target.name === show.name
                    ? { ...show, shown: true} 
                    : { ...show, shown: false}
            )
        );
        categorieData.map((subject) =>
            event.target.name === subject.name
                ? setVoteCount(categorieData.data)
                : {}
        );
    }

    function handleEvent(event){
        setSelectedOption(event.target.name);
    }

    return (
        <div id="subjectitems">
            <h3>{subject.subjectname}</h3>
            <p>{subject.subjectdescription}</p>
            <ul>
            {isShown.map((show, index) => (
                <li key={index}>
                    <button onClick={handleClick} name={show.name}>{show.name}</button>
                    {show.shown && (<Votings 
                                    selectedOption={selectedOption}
                                    handleEvent={handleEvent}
                                    voteCount={voteCount}
                                    setVoteCount={setVoteCount}/>)}
                </li>
            ))}
        </ul>
        </div>
    );
}

// Onderwerpen: Eten, Drinken, Wonen, Vrije tijd en Films

export default Subject;
