import React, { useState } from "react";


function Cola(selectedOption, handleEvent, voteCount, setVoteCount){
    const colaBrands = ['Coca', 'Pepsi', 'Dr Pepper'];
    setVoteCount({ ...voteCount, name: colaBrands}, { ...voteCount});
    const [isShown, setIsShown] = useState(false);

    const handleClick = event => {

        setIsShown(true);
    };

    return (
        <div>
            {!isShown && (
                <div>
                    <label>
                        <input type="radio" name={colaBrands[0]} value={voteCount.count + 1} checked={selectedOption === "Coca"} onChange={handleEvent}/>
                        {voteCount.name}
                    </label>
                    <label>
                        <input type="radio" name={colaBrands[1]} value={voteCount.count + 1} checked={selectedOption === "Pepsi"} onChange={handleEvent}/>
                        {voteCount.name}
                    </label>
                    <label>
                        <input type="radio" name={colaBrands[2]} value={voteCount.count + 1} checked={selectedOption === "Dr Pepper"} onChange={handleEvent}/>
                        {voteCount.name}
                    </label>
                    <br></br>
                    <button onClick={handleClick}>Stem!</button>
                </div>
            )}
            {isShown && 
                (<p>
                    Resultaten:
                    <br></br>
                    {colaBrands[0]} cola: {voteCount}<br></br>
                    {colaBrands[1]} cola: {voteCount}<br></br>
                    {colaBrands[2]}: {voteCount}
                </p>)}
        </div>
    );
}

export default Cola;
