import React, { useState, useEffect, useMemo} from 'react';
import Votings from '../../components/Votings';

function Subject({categorieName, categorieData}) {
    const [voteCount, setVoteCount] = useState(categorieData);
    const [isShown, setIsShown] = useState()
    const [selectedOption, setSelectedOption] = useState('');
    //const memiozedSetShown = useMemo(() => setShown(), [shownSubjects])

    //const memoizedCallback = useCallback(param => fillVoteCount(param), [param])

    /*function setShown(){
        subject.categorieData.map((items) => 
            setIsShown({name: items.name, shown: false})
        );
    }*/

    useEffect(() => {
        setIsShown(categorieName.map(names => ({
            name: names,
            shown: false
        })));
    }, [categorieData]);


    useEffect(() => {
        setVoteCount(categorieData);
    }, [categorieData]);

    function handleClick(event){
        setIsShown(
            isShown.map((show) =>
                event.target.name === show.name
                    ? { ...show, shown: true} 
                    : { ...show, shown: false}
            )
        );
    }

    function handleEvent(event){
        setSelectedOption(event.target.name);
    }

    return (
        <>
            <ul>
            {isShown.map((show, index) => (
                <li key={index}>
                    <button onClick={handleClick} name={show.name}>{show.name}</button>
                    {show.shown && (<Votings 
                                    selectedOption={selectedOption}
                                    handleEvent={handleEvent}
                                    voteCount={voteCount}
                                    setVoteCount={setVoteCount}/>)}
                </li>
            ))}
            </ul>
        </>
    );
}

// Onderwerpen: Eten, Drinken, Wonen, Vrije tijd en Films

export default Subject;
